mapper.select:
	SELECT *
	FROM CART c,p_board p WHERE c.id = #{id}
	ORDER BY c.SEQ DESC

SELECT count(*)
	FROM CART c 
	WHERE id=#{id} AND pseq=#{pseq} and selopt = #{selOpt}

SELECT pcount
	FROM CART c 
	WHERE id=#{id} AND pseq=#{pseq} and selopt = #{selOpt}

SELECT SEQ, ID, PSEQ, PCOUNT 
FROM CART
WHERE seq = #{seq}
