mapper.select:
SELECT CSEQ, CCONTENT, DISCOUNT, ID, HIT FROM COUPON
where id = #{id}
order by cseq desc

SELECT CSEQ, CCONTENT, DISCOUNT, ID, HIT
FROM COUPON
WHERE cseq = #{cseq}

mapper.update:

		UPDATE COUPON 
		SET CCONTENT=#{ccontent}, DISCOUNT=#{discount} 
		WHERE CSEQ=#{cseq}

UPDATE COUPON
SET HIT = HIT+1
WHERE CSEQ=#{cseq}
