mapper.update:
UPDATE STOCK SET pseq

 =#{pseq}
=p_board_seq.currval

, main = #{main}
		WHERE pnum = #{pnum}

update P_BOARD set
HIT = HIT+1
where pseq = #{pseq}

mapper.select:
SELECT distinct nvl(pseq,0) FROM (SELECT pseq FROM BUY_LIST bl ,BUY_DETAIL bd WHERE bl.BSEQ =bd.BSEQ AND bl.ID =#{id} AND bd.PSEQ = #{pseq}
UNION ALL
SELECT null as pseq FROM dual ) WHERE pseq IS NOT NULL OR rownum=1

	  SELECT * 
FROM (
	SELECT ROWNUM RN, A.*
		FROM (
			SELECT eb.ESEQ,if2.IMGNAME FROM E_BOARD eb,IMG_FILE if2 
			WHERE eb.ESEQ = if2.ESEQ
			ORDER BY eb.eseq DESC 
 	) A
		)
WHERE RN BETWEEN #{start} and #{end}
