mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT DISTINCT p.PSEQ ,p.PTITLE ,p.PDATE ,p.PCONTENT ,i.IMGNAME ,s.PRICE FROM p_BOARD p ,IMG_FILE i,STOCK s where p.PSEQ = i.pseq(+) AND p.PSEQ =s.PSEQ
ORDER BY p.pdate DESC
ORDER BY s.price ASC
ORDER BY s.price DESC
ORDER BY p.pseq asc
) A ) WHERE RN BETWEEN #{start} and #{end}
