mapper.select:
SELECT PNUM, PNAME, SCOUNT, COST, CNUM, PCONTENT, PRICE, PSEQ
FROM STOCK
WHERE pnum=#{pnum}

SELECT CNUM
FROM P_CATEGORY
WHERE ptype=#{ptype} and pcolor=#{pcolor} and psize=#{psize}

mapper.update:
UPDATE STOCK
SET PNAME=#{pname}, SCOUNT=#{scount}, COST=#{cost}, PCONTENT=#{pcontent}, PRICE=#{price}
WHERE PNUM=#{pnum}
